import React from 'react';
import logo from './logo.svg';
import './App.css';
import axios from "axios";
import { useState } from 'react';
import Card from './components/Card.js';

const QUESTIONS = [
  {
    id: 1,
    question: "What condition has symptoms that include heavy, painful periods?",
    answerOptions: [
      { answerText: 'Brain Cancer', isCorrect: false },
      { answerText: 'Liver Cancer', isCorrect: false },
      { answerText: 'Endometrosis', isCorrect: true },
      { answerText: 'Diabetes', isCorrect: false },
    ]
  },
  {
    id: 2,
    question: "Where are the fallopian tubes??",
    answerOptions: [
      { answerText: 'In the stomach', isCorrect: false },
      { answerText: 'In the uterus', isCorrect: true },
      { answerText: 'In the fingers', isCorrect: false },
      { answerText: 'In the toes', isCorrect: false },
    ]
  }
];


const App = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
	const [showScore, setShowScore] = useState(false);
	const [score, setScore] = useState(0);

  const cardChange = (isCorrect) => {
		if (isCorrect) {
			setScore(score + 1);
		};

		const nextQuestion = currentQuestion + 1;
		if (nextQuestion < QUESTIONS.length) {
			setCurrentQuestion(nextQuestion);
		} else {
			setShowScore(true);
		};
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Quterus</h1>
        <h2>How well do you know your uterus?</h2>
      </header>
      <main>

      </main>
    </div>
  );
};

export default App;
