import React from 'react';
import './App.css';
import { useState } from 'react';
import Cards from './components/Cards.js';

const QUESTIONS = [
  {
    topic: "Menstrual Cycle",
    question: "Question 1?",
    answerOptions: [
        {
            answerText: "Answer 1",
            isCorrect: true,
            _id: "6497122d8b7852503441b051"
        },
        {
            answerText: "Answer 2",
            isCorrect: false,
            _id: "6497122d8b7852503441b052"
        },
        {
            answerText: "Answer 3",
            isCorrect: false,
            _id: "6497122d8b7852503441b053"
        },
        {
            answerText: "Answer 1",
            isCorrect: false,
            _id: "6497122d8b7852503441b054"
        }
    ],
    _id: "6497122d8b7852503441b050",
    __v: 0
},
  {
    id: 2,
    questionText: "Where are the fallopian tubes??",
    answerOptions: [
      { answerText: 'In the stomach', isCorrect: false },
      { answerText: 'In the uterus', isCorrect: true },
      { answerText: 'In the fingers', isCorrect: false },
      { answerText: 'In the toes', isCorrect: false },
    ]
  }
];


const App = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
	const [showScore, setShowScore] = useState(false);
	const [score, setScore] = useState(0);

  const cardChange = (isCorrect) => {
		if (isCorrect) {
			setScore(score + 1);
		};

		const nextQuestion = currentQuestion + 1;
		if (nextQuestion < QUESTIONS.length) {
			setCurrentQuestion(nextQuestion);
		} else {
			setShowScore(true);
		};
  }

  return (
    <body class="page">
      <header>
        <h1>Quterus</h1>
        <h2>How well do you know the uterus?</h2>
      </header>
      <div class="content">
        <main class="contentMain">
          <div class="uterus-graphic">UC Center</div>
          <div class="cards">
            <Cards questions={QUESTIONS} currentQuestion={currentQuestion} showScore={showScore} score={score} cardChange={cardChange}/>
          </div>
        </main>
        <nav class="nav">
          Nav
        </nav>
      </div>
      <footer>Footer</footer>
    </body>
  );
};

export default App;
