import React from 'react';
import './App.css';
import { useState } from 'react';
import Cards from './components/Cards.js';

const QUESTIONS = [
  {
    id: 1,
    questionText: "What condition has symptoms that include heavy, painful periods?",
    answerOptions: [
      { answerText: 'Brain Cancer', isCorrect: false },
      { answerText: 'Liver Cancer', isCorrect: false },
      { answerText: 'Endometrosis', isCorrect: true },
      { answerText: 'Diabetes', isCorrect: false },
    ]
  },
  {
    id: 2,
    questionText: "Where are the fallopian tubes??",
    answerOptions: [
      { answerText: 'In the stomach', isCorrect: false },
      { answerText: 'In the uterus', isCorrect: true },
      { answerText: 'In the fingers', isCorrect: false },
      { answerText: 'In the toes', isCorrect: false },
    ]
  }
];


const App = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
	const [showScore, setShowScore] = useState(false);
	const [score, setScore] = useState(0);

  const cardChange = (isCorrect) => {
		if (isCorrect) {
			setScore(score + 1);
		};

		const nextQuestion = currentQuestion + 1;
		if (nextQuestion < QUESTIONS.length) {
			setCurrentQuestion(nextQuestion);
		} else {
			setShowScore(true);
		};
  }

  return (
    <body class="page">
      <header>Header</header>
      <div class="content">
        <main class="contentMain">
          <div class="ucleft">UC Left</div>
          <div class="uccenter">UC Center</div>
          <div class="ucright">UC Right</div>
          <div class="middlecontent">Middle Content</div>
        </main>
        <nav class="nav">Nav</nav>
      </div>
      <footer>Footer</footer>
    </body>
  );
};

export default App;
